function bubble_sort(&$arr){
	$len = count($arr);
	for($i = 0;$i < $len-1;$i++){
		for($j = $i;$j < $len;$j++){
			if($arr[$i] > $arr[$j]){
				$temp = $arr[$i];
				$arr[$i] = $arr[$j];
				$arr[$j] = $temp;
			}
		}
	}
}
$arr = array(9,2,8,3,7,4,6,5,1);
bubble_sort($arr);
var_dump($arr);
