function quick_sort($arr){
	$len = count($arr);
	if($len <= 1) return $arr;
	$key = $arr[0];
	$left_arr = array();
	$right_arr = array();
	for($i=1;$i<$len;$i++){
		if($arr[$i] <= $key){
			$left_arr[] = $arr[$i];
		}else{
			$right_arr[] = $arr[$i];
		}
	}
	$left_arr = quick_sort($left_arr);
	$right_arr = quick_sort($right_arr);
	return array_merge($left_arr,array($key),$right_arr);
}
$arr = array(9,2,8,3,7,4,6,5,1);
$array = quick_sort($arr);
